class Solution:
    def numberOfAlternatingGroups(self, c: List[int], k: int) -> int:
        left = 0
        res = 0
        c = c + c[:k-1]
        for i in range(1, len(c)):
            if c[i] == c[i-1]:
                left = i
                continue
            if i - left + 1 >= k:
                res = res + 1
        return res
