class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,n=colors.size(),c=0,t=0;
        for(int i=0;i<n+k-2;i++){//01010 ,3 123 a=1,2
            if(colors[i%n]!=colors[(i+1)%n]){
                c++;
            }else{
                c=0;
                t=i;
            }
            if((i-k)>=t&&i>=k&&(colors[(i-k+n)%n]!=colors[(i-k+1+n)%n])) c--;
            if(c>=(k-1)) ans++;
        }
        return ans;
    }
};
