class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        valid_groups = 0
        expected_changes = k - 1  

        change_count = 0
        for i in range(1, k):
            if colors[i - 1] != colors[i]:
                change_count += 1

        if change_count == expected_changes:
            valid_groups += 1

        for i in range(k, n + k - 1):
            if colors[(i - k) % n] != colors[(i - k + 1) % n]:
                change_count -= 1
            if colors[(i - 1) % n] != colors[i % n]:
                change_count += 1

            if change_count == expected_changes and i < n + k - 1: 
                valid_groups += 1

        return valid_groups
