class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        for(int i=0;i<k-1;i++){
            v.push_back(v[i]);
        }
        n=v.size();
        int ans=0,invalid=0;
        for(int i=1;i<k;i++){
            if(v[i] == v[i-1]) invalid++;
        }
        if(invalid == 0) ans++;
        for(int i=k;i<n;i++){
            int prev=i-k;
            if(v[prev] == v[prev+1]) invalid--;
            if(v[i]==v[i-1]) invalid++;
            if(invalid == 0) ans++;
        }
        return ans;
    }
};
