class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        for(int i=0; i<k-1; i++)
        {
            c.push_back(c[i]);
        }
        int n = c.size();

        vector<int> pf(n, 0);
        pf[0] = 0; pf[n-1] = 0;
        for(int i=1; i<n-1; i++)
        {
            if(c[i]!=c[i-1] && c[i]!=c[i+1])
            {
                pf[i] = pf[i-1] + 1;
            }
            else pf[i] = pf[i-1];
        }

        int ans = 0;
        int i=0, j=k-1; 
        while(j<n)
        {
            int alt = pf[j-1] - pf[i];
            if(alt == k-2) ans++;
            i++;
            j++;
        }
        return ans;
    }
};
