class Solution:
    # greedy, time O(n+k), space O(n+k)
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors.extend(colors[: k-1])
        A = [colors[i] != colors[i + 1] for i in range(len(colors) - 1)]
        res = 0
        for x, g in groupby(A):
            if x:
                res += max(0, len(list(g)) - k + 2)
        return res
