class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>a;
        for(auto it:colors) a.push_back(it); int t=k-1;
         for(auto it:colors) {a.push_back(it); t--; if(t==0) break;}
        int ans=0; 
        int i=0,j=1;int cnt=1;
        while(j<a.size()){
            if(a[j-1]!=a[j]){cnt++;}
            else {i=j;cnt=1;}
            if(j-i+1==k){
                ans++;
                i++;
            }
            j++;
        }
        return ans;


    }
};
