class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size() ;
        int ans = 0 ;
        vector<int>temp = colors ;
        for(int i = 0 ; i<k-1 ; i++)
        {
            temp.push_back(temp[i]);
        }

        int nn = temp.size() ;
        vector<int>dp(nn,0);
        dp[nn-1] = 1 ;
        for(int i = nn-2 ; i>=0 ; i--)
        {
            if(temp[i] != temp[i+1])
            {
                dp[i] = dp[i+1] + 1;
            }
            else
            {
                dp[i] = 1 ;
            }
        }

        for(int i = 0 ; i<n ; i++)
        {
            if(dp[i] >= k)
            {
                ans++;
            }
        }
        return ans ;
    }

};
