class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n = col.size();
        for(int i=0;i<k-1;i++){
            col.push_back(col[i]);
        }
        // for(auto it:col)cout<<it<<" ";
        cout<<endl;
        int left = 0;
        int right = 0;
        int ans = 0;
        while(left<n and right<n+k-1){
            if(right-left+1>=k){
                ans++;
            }
            if(right==n+k-2)break;
            if(col[right]==col[right+1]){
                left = right+1;
            }
            right++;
        }
        return ans;
    }
};
