class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        for(int i=0;i<n;i++)arr.push_back(arr[i]);
        int i=0;
        int j = 1;
        int cnt = 0;
        while(i<n){
            while((j-i+1)<=k){
                if(arr[j]!=arr[j-1])j++;
                else{
                    i = j-1;
                    //j++;
                    break;
                }
            }
            if((j-i+1)>=k){
                //cout << i << endl;
                cnt++;
            }
            i++;
            if(i>=j)j=i+1;
        }
        return cnt;
    }
};
