class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int ans = 0;
        int n = c.size();
        int prev = 0;
        for(int i = 0 ; i<n ; i++){
            int f = 1,ind=-1;
            int j = i+1;
            if(prev==1){
                j = i+k-2;
            }
            for( ; j<=k+i-2 ; j++){
                if(c[(j-1)%n]==c[(j+1)%n] && c[j%n]!=c[(j-1)%n]) continue;
                f = 0;
                ind = j;
                break;
            }
            if(f){
                prev = 1;
                ans++;
            }
            else{
                prev = 0;
                i = ind-1;
            }
        }
        return ans;
    }
};
