#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        int groupCount = 0;

        if (k == 1) {
            return N;
        }

        int currentLength = 1;
        int index = 1;
        while (index < N + k - 1) {
            int current = index;
            int previous = index - 1;

            if (current >= N) {
                current -= N;
            }
            if (previous >= N) {
                previous -= N;
            }

            if (colors[current] != colors[previous]) {
                ++currentLength;
            } else {
                currentLength = 1;
            }

            if (currentLength >= k) {
                ++groupCount;
            }

            ++index;
        }

        return groupCount;
    }
};
