class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = (int)colors.size();
        vector<int> a(n);
        for (int i = 0; i < n; i++) {
            int nxt = (i + 1) % n;
            if (colors[i] != colors[nxt]) {
                a[i] = 1;
            }
        }

        vector<int> p(n);
        p[0] = a[0];
        for (int i = 1; i < n; i++) {
            p[i] = p[i - 1] + a[i];
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            int j = (i + k - 2) % n;
            int sum = 0;
            if (j > i) {
                sum += p[j] - (!i ? 0 : p[i - 1]);
            } else {
                sum += p[n - 1] - (!i ? 0 : p[i - 1]);
                sum += p[j];
            }
            if (sum == k - 1) {
                ans++;
            }
        }

        return ans;
    }
};
