class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size();
        for(int i=0; i<n; i++){
            nums.push_back(nums[i]);
        }
        int i=0;
        int j=0;
        int cnt=0;
        while(j<nums.size()){
            if(i>=n){
                break;
            }
            if(j==0 || nums[j]!=nums[j-1]){
                if(j-i+1<k){
                    j++;
                }else{
                    cnt++;
                    i++;
                    j++;
                }
            }else{
                i=j;
                j++;
            }
        }
        return cnt;
    }
};
