class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size() ;
        for (int i = 0 ; i < k-1 ; i++){
            colors.push_back(colors[i]) ;
        }
        vector<int> v ;
        int t = 1 ;
        for (int i = 1 ; i < colors.size() ; i++){
            if (colors[i] != colors[i-1]){
                t++ ;
            }
            else {
                v.push_back(t) ;
                t = 1 ;
                if (i >= n){
                    break ;
                }
            }
        }
        v.push_back(t) ;

        int ans = 0 ;
        for (int i = 0 ; i < v.size() ; i++){
            if (v[i] >= k){
                ans += (v[i]-k+1) ;
            }
        }
        return ans ;
    }
};
