class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& array, int k) {
    int n = array.size();
    vector<int> newArray(n + k - 1);
    for (int i = 0; i < n; ++i) {
        newArray[i] = array[i];
    }
    for (int i = 0; i < k - 1; ++i) {
        newArray[n + i] = array[i];
    }
    int count = 0;
    int currentDistinctCount = 1;
    for (int i = 1; i < newArray.size(); ++i) {
        if (newArray[i] != newArray[i - 1]) {
            ++currentDistinctCount;
        } else {
            currentDistinctCount = 1;
        }
        if (currentDistinctCount >= k) {
            ++count;
        }
    }
    return count;
    }
};
