class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k)
    {
        int parts = colors.size();
        int req = 0;
        int ptr = 0;
        int ptr2 = 1;
        //just calculate for k=2
        int ans2=0;
        for(int i=0;i<parts;i++)
        {
            if(colors[i]!=colors[(i+1)%parts])
            {
                ans2++;
            }
        }
        //cout<<ans2;
        //just calculate for k=3
        int ans3=0;
        for(int i=0;i<parts;i++)
        {
            if(colors[i]==colors[(i+2)%parts] && colors[i]!=colors[(i+1)%parts])
            {
                ans3++;
            }
        }
        //cout<<ans3;
        while (ptr < parts && ptr2-k < parts)
        {
            int dummy = ptr2;
            int dummy2 = dummy % parts;
            while (dummy + 1 < (parts + k) && colors[(dummy2 - 1 + parts) % parts]!=colors[dummy2])
            {
                dummy2 = (dummy+1) % parts;
                dummy++;
            }
            if(dummy - ptr + 1 <= k)
            {
                ;
            }
            else
            {
                req = req + dummy - ptr - k + 1;
            }
            ptr = dummy;
            ptr2 = ptr + 1;
        }
        return req;
    }
};
