class Solution(object):
    def numberOfAlternatingGroups(self, colors, k):

        N = len(colors)
        cnt = 0
        if k == 1:
            return N

        aLe = 1
        i = 1
        while i < N + k - 1:
            cR = i % N
            pR = (i - 1) % N

            if colors[cR] != colors[pR]:
                aLe += 1
            else:
                aLe = 1

            if aLe >= k:
                cnt += 1

            i += 1

        return cnt
