class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n= colors.size();

        int ppp =n+k-1;


        vector<int> c(ppp);

        for(int i=0; i<n; i++){
            c[i] = colors[i];
        }


        for(int i=n; i<ppp; i++){
            c[i]=colors[i-n];
        }

        vector<int> pre(ppp);

        int count=1;
        pre[0]=1;

        for(int i=1; i<ppp; i++){

        if(c[i]!= c[i-1]){

            count++;
        }
        else{

            count=1;
        }
        pre[i]=count;

        }

        long long res ;
        for(int i=0; i<ppp; i++){

            if(pre[i] >= k) res++;
        }
        return res;

    }
};
