class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int len=nums.size(),ans=0;
        for(int i=0;i<len;){
            int j=i+1;
            while(j%len!=i&&nums[j%len]!=nums[(j-1)%len]){
                j++;
            }
            int l=j-i;
            if(j%len==i&&nums[i]!=nums[(i+len-1)%len]){
                ans+=len;
                break;
            }
            //cout<<(l-k+1)<<" "<<j<<" "<<i<<endl;
            ans+=min(max(l-k+1,0),len-i);
            i=j;
        }
        return ans;
    }
};
