class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        res = 0
        n = len(colors)
        start = 0
        end = -1
        while start < n:
            idx = start
            while colors[idx % n] != colors[(idx + 1) % n]:
                idx += 1
                if (idx + 1) % n == start or (idx + 1) == n + k - 1:
                    break

            res += max(idx - start + 1 - k + 1, 0)

            if idx + 2 - k > start and (idx + 1) % n == start:
                start = idx + 2 - k
            else:
                start = idx + 1

        return res
