class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        res = 0

        length = 1
        for j in range(1, n+k-1):
            if colors[j%n] != colors[(j-1)%n]:
                length += 1
            else:
                length = 1
            if length >= k:
                res += 1
        return res
