class Solution:
    def numberOfAlternatingGroups(self, A: List[int], k: int) -> int:
        B = A + A
        n = len(B)

        ans = 0

        dp = [0]*n
        dp[n-1] = 1

        for i in range(n-2, -1, -1):
            if B[i] != B[i+1]:
                dp[i] += 1 + dp[i+1]
            else:
                dp[i] = 1
            if i <= n//2-1 and dp[i] >= k: ans += 1

        return ans
