class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;

        int n = colors.size();
        for(int i=0; i<n; i++) {
            colors.push_back(colors[i]);
        }

        int m = colors.size();

        auto dp = vector<int>{0,0};

        for(int i=0; i<m; i++) {
            dp[colors[i]] = dp[colors[i]^1] + 1;
            dp[colors[i]^1] = 0;

            if(i>=k-1 && i<n-1+k && dp[colors[i]]>=k) {
                ans += 1;
            }
        }

        // 0 1 2 ... n-1 n ... 

        return ans;
    }
};
