class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i < k - 1; ++i){
            colors.push_back(colors[i]);
        }

        int n = colors.size();
        vector<int> dp(n, 0);
        for(int i = 0; i < n - 1; ++i){
            if(colors[i] != colors[i + 1]){
                dp[i] = 1;
            }
        }

        int res = 0;
        int count = 0;
        for(int i = 0; i < n; ++i){
            count = (dp[i] == 1) ? count + 1 : 0;
            res += (count >= k - 1);
        }
        return res;
    }
};
