class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& c, int k) { 
        int n = c.size(); 

        if (k == 1)  
            return n; 

         int ans = 0; 
        int len = 1; 
        int i = 1; 
        while (i < n + k - 1) 
        { 

            int pre = i - 1; 
             int cur = i;

            if (cur >= n) 
            { 
                cur -= n; 
            } 
            if (pre >= n) 
            { 
                pre -= n; 
            } 

            if (c[cur] != c[pre]) 
            { 
                len++; 
            } 
            else 
            { 
                len = 1; 
            } 


            if (len >= k) 
            { 
                ans++; 
            } 

            i++; 
        } 

        return ans; 
    } 
};



