class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        vector<int> a;

        int n=colors.size();
        for(int i=0;i<n;i++)
        {
            a.push_back(colors[i]);
        }
        for(int i=0;i<n;i++)
        {
            a.push_back(colors[i]);
        }

        int m=a.size();
        vector<int> xors(m);
        vector<int> prefix(m,0);

        for(int i=1;i<m;i++)
        {
            xors[i]=(a[i]^a[i-1]);
        }

        for(int i=1;i<m;i++)
        {
            prefix[i]=prefix[i-1]+xors[i];
        }

        int ans=0;

        for(int i=0;i<n;i++)
        {
            //check whether [i...i+k-1] is alternating
            int end=prefix[i+k-1];
            int start=prefix[i];

            if(end-start==k-1)
            {
                ++ans;
            }
        }

        return ans;

    }
};
