class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i =0,j=0,g=0;
        int n = colors.size();
        bool a=true;
        while(i<n){

            if((j!=0 || !a ) && colors[j]==colors[((j-1)%n + n )%n]){
                 if(i<j)
                    i=j;
                 else
                     break;
            }

            if((j-i+1)==k || (n-i+ j+1)==k){
                g++;
                i++;
            }
            j++;
            j=j%n;
            if(j==0){
                a=false;
            }

        }
        return g;


    }
};
