class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        same = [1]*n
        for i in range(1, n+k-1):
            if colors[i%n] == colors[(i-1)%n]:
                same[i%n] = same[(i-1)%n] + 1
        # print(same)

        ans = 0
        alter_ones = [0]*n
        for i in range(1, n+k-1):
            if same[i%n] == 1:
                alter_ones[i%n] = alter_ones[(i-1)%n]+1
            else:
                alter_ones[i%n] = 0
            if alter_ones[i%n] >= k-1:
                ans += 1
        # print(alter_ones)
        return ans
