class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans = 0, i = 0, j = 1, n = v.size();
        while (j < n + k && i < n) {
            int left = j;
            int right = left % n;
            while (v[right] != v[(right - 1 + n) % n] && left < n + k - 1) {
                left++;
                right = left % n;
            }
            ans += max(left - i - k + 1, 0);
            i = left;
            j = i + 1;
        }
        return ans;
    }
};
