class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++) colors.push_back(colors[i]);
        int ans = 0;
        int n = colors.size();
        vector<int> dp(n,0);
        dp[0]=1;
        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]) dp[i]=dp[i-1]+1;
            else dp[i]=1;
            if(i>=k-1){
                if(dp[i]>=k) ans++;
            }
        }
        return ans;
    }
};
