class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        set<int> ind;
        for(int i = 0; i < k - 1; i++) {
            if(a[i]==a[i + 1]) {
                ind.insert(i);
            }
        }
        int ans = 0;
        if(ind.empty())
            ans++;
        for(int i = 1; i < n; i++) {
            int prv = (i - 1 + n) % n;
            if(ind.find(prv) != ind.end()) {
                ind.erase(ind.find(prv));
            }
            int n1 = (i + k - 2) % n, n2 = (i + k - 1) % n;
            if(a[n1]==a[n2]) {
                ind.insert(n1);
            }
            if(ind.empty())
                ans++;
        }
        return ans;
    }
};
