class Solution{
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k)
    {
        for (int i = 0; i < k - 1; i++)
            colors.push_back(colors[i]);
        int ans = 0, curr = 1, col = colors.size();
        for (int i = 1; i < col; i++)
        {
            if (colors[i] == colors[i - 1])
            {
                if (curr >= k){
                    ans = ans + curr - k + 1;
                }
                curr = 1;
            }
            else{
                curr++;
            }
        }
        if (curr >= k){
            ans = ans + curr - k + 1;
        }
        return ans;
    }
};
