class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size=colors.size();
        for(int i =0;i<size;i++){
            colors.push_back(colors[i]);
        }
        int i =0,j=0;
        int ans=0;
        while(i<size){
            while(j-i+1<=k){
                j++;
                if(j-i==k) break;
                if(colors[j]!=colors[j-1])continue;
                else{
                    i=j;
                    break;
                }
            }
            if(j-i==k){
                while(i<size){
                    ans++;
                    if(colors[j]==colors[j-1]){
                        i=j;
                        break;
                    }
                    i++;
                    j++;
                }
            }
        }
        return ans;



    }
};
