class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> a;
        for(int i=n-k+1;i<n;i++)
            a.push_back(colors[i]);
        for(int i=0;i<k-1;i++)
            a.push_back(colors[i]);
        vector<int> dp1(a.size()+1,0);
        dp1[0]=1;
        for(int i=1;i<a.size();i++)
            if(a[i]!=a[i-1])
               dp1[i]=dp1[i-1]+1;
            else dp1[i]=1;
        vector<int> dp(n+1,0);
        dp[0]=1;
        for(int i=1;i<n;i++)
            if(colors[i]!=colors[i-1])
               dp[i]=dp[i-1]+1;
            else dp[i]=1;
        int res=0;
        for(int i=0;i<n;i++)
            if(dp[i]>=k)
              res++;

        for(int i=0;i<a.size();i++)
            if(dp1[i]>=k)
              res++;
        return res;         
    }
};
