class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        for(int i=0;i<k-1;i++) colors.push_back(colors[i]);
        int n = colors.size();

        int i=0;
        int j=0;

        int ans = 0;
        while(i<n-1){
            int j=i+1;
            if(colors[i]==colors[j]){
                i++;
                j++;
                continue;
            }
            while(j<n && colors[j]!=colors[j-1]){
                j++;
            }
            ans+=max(0,j-i-k+1);
            // cout<<i<<" "<<j<<endl;
            i=j;
        }

        return ans;
    }
};
