class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int i=0;
        int j=0;
        int n=nums.size();
        int prev=-1;
        int cnt=0;
        while(i<n&&j<n-1+k){
            if(prev==-1||!(prev)==nums[j%n]){
                if(j-i+1==k){
                  cnt++; 
                  j++;
                }
                else if(j-i+1>k){
                    while(i<=j&&j-i+1>k){
                        i++;
                    }
                    cnt++;
                    j++;
                }
                else{
                    j++;
                }

            }
            else{
                i=j;
                j++;
            }
            prev=nums[(j-1)%n];
        }
        return cnt;
    }
};
