class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int x = 0;
        vector<int> a(n,0);
        for(int i=1;i<n-1;i++){
            if(v[i]!=v[i-1]&&v[i]!=v[i+1])a[i]=1;
        }
        if(v[n-1]!=v[n-2]&&v[n-1]!=v[0])a[n-1]=1;
        if(v[0]!=v[1]&&v[0]!=v[n-1])a[0]=1;
        for(int i=1;i<n;i++){
            if(a[i]==1)a[i]+=a[i-1];
        }
        // for(int i=0;i<n;i++)cout<<a[i]<<" ";
        // cout<<endl;
        int f = 0;
        reverse(a.begin(),a.end());
        int mx = 0;
        for(int i=0;i<n;i++){
            if(a[i]!=0){
                mx=max(a[i],mx);
                a[i]=mx;
            }
            else mx = 0;
        }
        reverse(a.begin(),a.end());
        if(a[0]&&a[n-1])a[n-1]=a[0]+a[n-1];
        reverse(a.begin(),a.end());
        mx = 0;
        for(int i=0;i<n;i++){
            if(a[i]!=0){
                mx=max(a[i],mx);
                a[i]=mx;
            }
            else mx = 0;
        }
        reverse(a.begin(),a.end());
        for(int i=0;i<n;i++)cout<<a[i]<<" ";
        cout<<endl;
        int ans = 0;
        for(int i=0;i<n-1;i++){
            if(a[i]==0&&a[i+1]!=0){
                f=1;
                    int p = a[i+1]+2;
                    if(p>k)ans+=(p-k+1);
                    else if(p==k)ans++;
            }
        }
        if(a[0]!=0&&a[n-1]==0){
            for(int i=0;i<n-1;i++){
                if(a[i]!=0&&a[i+1]==0){
                    f=1;
                    int p = a[i]+2;
                     if(p>k)ans+=(p-k+1);
                    else if(p==k)ans++;
                    break;
                }
            }
        }

        if(f==0){
            int p = 0;
            for(int i=0;i<n;i++)if(a[i])p=1;
            if(p==0)return 0;
            return n;
        }
        return ans;;
    }
};
