class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
         int n=colors.size();
         int cnt=1;
         int ans=0;
         for(int i=1;i<n+k-1;i++){
            int index=i%n;
            if(colors[index]!=colors[(index-1+n)%n]){
                cnt++;
            }
            else{
                cnt=1;
            }
            if(cnt>=k){
                ans++;
            }
         }
         return ans;

    }
};
