class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

            int  n=colors.size();
            int cnt=0;
            if (k==1){
                    return n;
            }
            int len=1;
            int i=1;

                while (i-k+1<n){
                        int curr=i;
                        int pre=curr-1;
                        if (curr>=n){
                                curr=curr%n;
                        }
                        if (pre>=n){
                                pre=pre%n;
                        }
                        if (colors[curr]!=colors[pre]){
                                len+=1;
                        }
                        else {
                                len=1;
                        }
                        if (len>=k){
                                cnt+=1;
                        }
                        i+=1;
        }

        return cnt; 


    }
};
