class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int i=0,j=0;
        int ans=0;
        bool flag= c[0]==0 ? true : false;
        while(i<c.size() && j<2*c.size()){
            if(flag==c[j%c.size()]) i=j;
            int len= (j-i+1);
            if(len==k){
                ans++;
                i++;
            }
            flag= c[j%c.size()];
            j++;
        }
        return ans;
    }
};
