class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        int ans=0;
        int al=0;
        for (int i=1; i<k; i++) 
        {
            if (c[i]!=c[i-1]) al++;
        }
        if (al==k-1) ans++;
        for (int i=1; i<n; i++)
        {
            if (c[(i-1)%n]!=c[i%n]) al--;
            if (c[(i+k-1)%n]!=c[(i+k-2)%n]) al++;
            if (al==k-1) ans++;
        }
        return ans;

    }
};
