class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;

        vector<int> arr(colors.begin(), colors.end());

        n = arr.size();
        vector<bool> valid(n, false);
        for (int i = 0; i < n; i++) {
            arr.push_back(colors[i]);
            // int prev = (i - 1) % n;
            // if (prev < 0)
            //     prev += n;

            // int next = (i + 1) % n;

            // if (arr[prev] == arr[next] && arr[prev] != arr[i]) {
            //     valid[i] = true;
            //     valid[i + n] = true;
            // }
        }

        int i = 0, j = 1;
        while (i < colors.size() && j < arr.size()) {
            if (arr[j] == arr[j - 1]) {
                i = j;
                j++;
            } else if (j - i + 1 == k)
                count++, i++, j++;
            else
                j++;
        }

        return count;
    }
};
