class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
int n = colors.size();
    int start = 0;
    int end = 1;
    int count = 0;
    map<int, int> colorCount;

    colorCount[colors[0]]++;

    while (end < n + k - 1) {
        if (colors[end % n] != colors[(end - 1) % n]) {
            if (end - start + 1 == k) {
                count++;
                colorCount[colors[start]]--;
                if (colorCount[colors[start]] == 0) {
                    colorCount.erase(colors[start]);
                }
                start++;
            }
            colorCount[colors[end % n]]++;
        } else {
            start = end;
            colorCount.clear();
            colorCount[colors[end % n]]++;
        }
        end++;
    }

    return count;

    }
};
