class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i<k-1;i++)colors.push_back(colors[i]);
        int start = 0;
        int curr= 1;
        int res= 0;
        // cout<<colors.size()<<endl;
        while(curr<n+k-1){
            if(colors[curr]==colors[curr-1]){
                start = curr;
            }
            else{
                if(curr-start==k-1){
                    // cout<<curr<<" ";
                    res++;
                    start++;
                }
            }
            curr++;
        }
        return res;
    }
};
