    int get(int l,int r,vector<int> &x){
        int sm=x[r];
        if(l){
            sm-=x[l-1];
        }
        return sm;
    }

    int numberOfAlternatingGroups(vector<int>& a, int k) {
        vector<int> b=a;
        for(auto i:b){
            a.pb(i);
        }
        int n=sz(b);
        vector<int> zo(2*n,0);
        vector<int> oz(2*n,0);
        for(int i=0;i<2*n;++i){
            if(i%2){
                if(a[i]){
                    zo[i]=1;
                }
                else{
                    oz[i]=1;
                }
            }
            else{
                if(a[i]){
                    oz[i]=1;
                }
                else{
                    zo[i]=1;
                }
            }
        }



        for(int i=1;i<2*n;++i){
            if(oz[i]){
                oz[i]+=oz[i-1];
            }

            if(zo[i]){
                zo[i]+=zo[i-1];
            }
        }


        long long fin=0;

        for(int i=0;i<n;++i){
            int l=i;
            int r = i+k-1;
            int s1 = get(l,r,oz);
            int s2 = get(l,r,zo);
            fin+=(s1==k);
            fin+=(s2==k);
        }
        return fin;

    }
