class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(), ans=0, curr=0, end=0, sz=n+k-1;
        vector<int> arr(sz), val(sz,0);
        for(int i=0; i<n; i++){
            arr[i]=colors[i];
        }
        for(int j=n,i=0; j<sz; i++,j++){
            arr[j]=colors[i];
        }
        for(int i=1; i<sz-1; i++){
            if(arr[i-1]==arr[i+1] && arr[i]!=arr[i-1]) val[i]=1;
        }
        end=k-2;
        for(int i=0; i<k-2; i++) if(val[i]) curr++;
        for(int i=0; end<sz; i++, end++){
            if(curr>=k-2) ans++;
            curr-=val[i];
            curr+=val[end];
        }
        return ans;
    }
};
