class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ans=0;
        int n=a.size();
        vector<int> b;
        for(int i=0;i<n;i++){
            b.push_back(a[i]);
        }
        for(int i=0;i<n;i++){
            b.push_back(a[i]);
        }
        for(int i=0;i<n;i++){
            b.push_back(a[i]);
        }
        int dp[3*n];
        dp[0]=1;
        for(int i=1;i<3*n;i++){
            if(b[i]==b[i-1]){
                dp[i]=1;
            }
            else{
                dp[i]=dp[i-1]+1;
            }
        }
        for(int i=n;i<2*n;i++){
            if(dp[i]>=k){
                ans++;
            }
        }
        return ans;
    }
};
