class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        bool flag = true;
        int count = 0, cur_len = 1;
        for (int i = n - 1; flag; --i) {
            if (cur_len >= k) {
                ++count;
                break;
            }
            if (colors[i] == colors[(i + 1) % n]) break;
            ++cur_len;
        }
        for (int i = 1; i < n; ++i) {
            if (colors[i] == colors[i - 1]) {
                cur_len = 1;
                continue;
            } else {
                ++cur_len;
                if (cur_len >= k) ++count;
            }
        }
        return count;
    }
};
