#include <vector>
#include <unordered_map>

class Solution 
{
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(), colors.begin(), colors.begin() + k);

        int n = colors.size(), count = 0;
        unordered_map<string, int> colorToIndex;
        vector<int> prefixDiffs(n + 1, 0);

        for (int i = 1; i < n; ++i) {
            prefixDiffs[i] += prefixDiffs[i - 1] + abs(colors[i]-colors[i-1]);
        }
        k--;
        for (int i = k+1; i < n; ++i) {
            if (prefixDiffs[i] - prefixDiffs[i - k] == k ) {
                count++;
            }
        }
        return count;
    }
};
